sys_script_client.script:
function onLoad() {
   /* O script verifica se o formulário esta aberto para um novo registro. Caso esteja,
   o script seta automaticamente o userID do usuario logado para o campo u_requested_for
   */
	jslog("THP: IRA VERIFICAR SE É UMA NOVA INSERCAO DE REGISTRO");
	if (g_form.isNewRecord()) {
		g_form.setValue('u_requested_for', g_user.userID);
		jslog("THP: É uma nova inserção, por isso setou o usuário logado no campo u_requested_for");
	}

	try {
			helloWorld();
	} catch (erro) {
		jslog("THP: Um novo erro foi lançado: " + erro.message);
	}
}
